DROP TABLE IF EXISTS `password_resets`;
CREATE TABLE `password_resets` (
  `email` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `token` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  KEY `password_resets_email_index` (`email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `report_lists`;
CREATE TABLE `report_lists` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `report_id` int NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `body` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `file` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `thumbnail` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'default.jpg',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `report_lists_report_id_index` (`report_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

INSERT INTO `report_lists` (`id`, `report_id`, `title`, `slug`, `body`, `file`, `thumbnail`, `created_at`, `updated_at`) VALUES
(1,	1,	'[HOAKS] Dokumen WHO Terkait Anjuran Tidak Menggunakan Masker Selama Pandemi Covid-19',	'hoaks-dokumen-who-terkait-anjuran-tidak-menggunakan-masker-selama-pandemi-covid-19-4klaH',	'<p>Penjelasan :</p>\r\n\r\n<p>Beredar sebuah dokumen di media sosial yang diklaim merupakan dokumen resmi Organisasi Kesehatan Dunia (WHO). WHO&nbsp; melalui dokumen tersebut merekomendasikan atau menganjurkan untuk tidak menggunakan masker selama pandemi Covid-19. Rekomendasi itu dibuat karena masker tidak terbukti efektif melindungi diri dari Covid-19.&nbsp;</p>\r\n\r\n<p>Faktanya, dikutip dari <a href=\"https://factcheck.afp.com/fake-who-document-shared-anti-mask-posts\">Factcheck.afp.com</a> dokumen yang diklaim berasal dari WHO berisi rekomendasi untuk tidak mengenakan masker di tengah pandemi Covid-19 adalah tidak benar. WHO melalui juru bicaranya, Margaret Harris mengatakan dokumen yang beredar di media sosial itu bukan dokumen WHO. WHO dan sejumlah badan kesehatan lainnya justru sangat merekomendasikan penggunaan masker untuk membantu melindungi diri dari Virus Corona baru (Covid-19).</p>',	NULL,	'laporan/Xft1Dl88Gg9LHwxkFcNJsb33oU9Ncj8WvBmYVhlC.jpeg',	'2020-08-24 07:30:46',	'2020-08-24 07:30:46'),
(2,	1,	'[DISINFORMASI] 35 Guru Meninggal Akibat Covid-19 di Surabaya',	'disinformasi-35-guru-meninggal-akibat-covid-19-di-surabaya-CMoVN',	'<p>Penjelasan :</p>\r\n\r\n<p>Telah beredar melalui pesan berantai pada platform Whatsapp, informasi mengenai 35 orang guru yang sudah meninggal akibat Covid-19 di kota Surabaya. Pesan berantai tersebut memiliki narasi, &ldquo;!!!!!....Perhatian bagi seluruh warga surabaya....!!! Sudah ada 35 guru dan tenaga kependidikan di Kota Surabaya meninggal dunia akibat Covid-19. Waspada jika ada kenalan, teman dan keluarga guru. Agar waspada bagi kita semua. &ldquo;</p>\r\n\r\n<p>Berdasarkan penelusuran, Ketua Umum Persatuan Guru Republik Indonesia (PGRI), Unifah Rosyidi pun membenarkan kabar meninggalnya puluhan guru tersebut. Meski secara angka dirinya masih ragu. Namun Pemkot Surabaya membantah kabar yang beredar bahwa terdapat 35 guru di Surabaya meninggal dunia akibat Covid-19. Berdasarkan data Dinas Kesehatan Kota Surabaya, total ada 4 guru yang meninggal dunia akibat terpapar Covid-19.</p>',	NULL,	'laporan/FGKvfXZBhe75HrnK3I0pQLCari9TwQvp3e6IYmwB.jpeg',	'2020-08-24 07:32:00',	'2020-08-24 07:32:00'),
(3,	1,	'[DISINFORMASI] Pernyataan Jokowi Pertumbuhan Ekonomi Indonesia Terjun Bebas Tidak Diberi Utang Lagi',	'disinformasi-pernyataan-jokowi-pertumbuhan-ekonomi-indonesia-terjun-bebas-tidak-diberi-utang-lagi-9q3X2',	'<p>Penjelasan :</p>\r\n\r\n<p>Sebuah akun media sosial Facebook membagikan gambar tangkapan layar dari laman artikel berita dengan judul &ldquo; Jokowi:&nbsp; Pertumbuhan Ekonomi Indonesia &ldquo;Terjun Bebas&rdquo; ngutang, enggak diberi lagi, gaess&rdquo;.</p>\r\n\r\n<p>Faktanya gambar hasil tangkapan layar tersebut adalah hasil editan. Dari hasil penelusuran, berita asli pada artikel tersebut berasal dari laman <a href=\"https://www.jpnn.com/news/jokowi-prediksi-pertumbuhan-ekonomi-indonesia-terjun-bebas?\">Jpnn.com </a>dengan judul &ldquo;Jokowi Prediksi Pertumbuhan Ekonomi Indonesia &quot;Terjun Bebas&quot;&rdquo;. Artikel tersebut tayang pada tanggal 14/4/2020 lalu. Pada bagian judul berita telah mengalami penyuntingan dengan menghilangkan dan menambahkan beberapa kata sehingga berbeda makna.</p>',	NULL,	'laporan/je7yPJIdfWFQLrSbssqWzjGnFSipW6z9UeVXM428.jpeg',	'2020-08-24 07:32:44',	'2020-08-24 07:32:44'),
(4,	1,	'[HOAKS] Formulir Online Pendaftaran Kartu Indonesia Pintar',	'hoaks-formulir-online-pendaftaran-kartu-indonesia-pintar-NNMLb',	'<p>Penjelasan :</p>\r\n\r\n<p>Beredar sebuah informasi berisi tautan formulir online pendaftaran Kartu Indonesia Pintar di media sosial. Edaran informasi tersebut disertai narasi &ldquo;Bagi yg punya putra putri, SD, SMP, SMA/K ingin mengajukan program Indonesia Pintar / Kartu Indonesia Pintar, silahkan mengisi link dibawah ini. Ditunggu sampai tanggal 25 Agustus 2020. Tks&rdquo;.&nbsp;</p>\r\n\r\n<p>Faktanya, informasi mengenai formulir online yang beredar tersebut dibantah oleh Kementerian Pendidikan dan Kebudayaan RI melalui unggahan story pada akun Instagram resmi <a href=\"https://www.instagram.com/kemdikbud.ri/\">@kemdikbud.ri</a>, Minggu, 23 Agustus 2020. Kemendikbud dalam unggahannya juga mengingatkan agar masyarakat berhati-hati terhadap formulir penipuan yang meminta data siswa tersebut.&nbsp;</p>',	NULL,	'laporan/oBMhKqOZH03myVg7FB5QGu7wO6ZXeaPGQ36KMqZu.jpeg',	'2020-08-24 07:33:31',	'2020-08-24 07:33:31'),
(5,	1,	'[DISINFORMASI] Gaji Tambahan dari Jokowi Berupa Mata Uang Yen',	'disinformasi-gaji-tambahan-dari-jokowi-berupa-mata-uang-yen-bhokP',	'<p>Penjelasan :</p>\r\n\r\n<p>Beredar postingan di media sosial yang berisi tangkapan layar sebuah artikel dengan judul &quot;Ada 13 Juta Orang yang Bakal Dapat Gaji Tambahan dari Jokowi Gaji Berupa Mata Uang Yen, Yen Ora Mbujuk Lagi&quot;.&nbsp;</p>\r\n\r\n<p>Faktanya dilansir dari <a href=\"https://www.liputan6.com/cek-fakta/read/4337413/cek-fakta-tidak-benar-gaji-tambahan-dari-jokowi-berupa-mata-uang-yen\">Liputan6.com</a>, judul berita yang menyebut tentang 13 juta orang bakal dapat gaji tambahan dari Jokowi berupa mata uang yen ternyata tidak benar. Judul pada gambar tangkapan layar tersebut adalah hasil editan atau suntingan. Adapun artikel aslinya berjudul &quot;Ada 13 Juta Orang yang Bakal Dapat Gaji Tambahan dari Jokowi&quot; yang dimuat situs <a href=\"https://www.cnbcindonesia.com/news/20200805174412-4-177706/ada-13-juta-orang-yang-bakal-dapat-gaji-tambahan-dari-jokowi\">Cnbcidonesia.com</a> pada 5 Agustus 2020.</p>\r\n\r\n<p>&nbsp;</p>',	NULL,	'laporan/6mm0tbbf8WvjkBokbfQ4ofse8kuivISCSLyoMrnp.jpeg',	'2020-08-24 07:38:51',	'2020-08-24 07:38:51'),
(9,	2,	'Laporan Keuangan 2018 Audited',	'laporan-keuangan-2018-audited-2ufx4',	'<p>Unduh&nbsp;Laporan Keuangan Kementerian Komunikasi dan Informatika Tahun 2018 (Audited) Menggunakan Link Di Atas</p>',	'file/0HIzqPOgBMN0jFFxmdoAucItLik8StxthQxQqgzK.pdf',	'default.jpg',	'2020-08-24 08:03:38',	'2020-08-24 08:07:05'),
(12,	2,	'Laporan Keuangan Kementerian Komunikasi dan Informatika Tahun 2017 (Audited)',	'laporan-keuangan-kementerian-komunikasi-dan-informatika-tahun-2017-audited-CiWEY',	'<p>Untuk Laporan Bisa Di Baca Dengan Link Di Atas</p>',	'file/ISGu2eqqcEfrLU44BipANTh4vAIECV10inR1Rtq2.pdf',	'laporan/ISa19qaBB13pZTs3DQsCltGgN19FP2A56ISHsLPq.png',	'2020-08-25 03:44:29',	'2020-08-25 03:44:29'),
(13,	2,	'Laporan Keuangan Kementerian Komunikasi dan Informatika Tahun 2016 (Audited)',	'laporan-keuangan-kementerian-komunikasi-dan-informatika-tahun-2016-audited-Kfo8i',	'<p>Untuk Laporan Bisa Di Baca Diatas</p>',	'file/1MCQ5kaLz7jag7Ek01bmkz1U6KbzXH3qwp2L8l3D.pdf',	'laporan/Ob19vzuMyefIX0p9F6YMu1U0txLYnwtWNasjlqvs.png',	'2020-08-25 03:46:26',	'2020-08-25 03:46:26'),
(14,	2,	'Laporan Keuangan Kementerian Komunikasi dan Informatika Tahun 2015',	'laporan-keuangan-kementerian-komunikasi-dan-informatika-tahun-2015-wOSDb',	'<p>Sebagaimana diamanatkan Undang-Undang nomor 17 Tahun 2003 tentang Keuangan Negara bahwa Menteri/Pimpinan Lembaga sebagai Pengguna Anggaran/Barang mempunyai tugas antara lain menyusun dan menyampaikan laporan keuangan Kementrian Negara/Lembaga yang dipimpinnya.</p>\r\n\r\n<p>Komunikasi dan Informatika adalah salah satu entitas pelaporan sehingga berkewajiban menyelenggarakan akuntansi dan laporan pertanggungjawaban atas pelaksanaan Anggaran Pendapatan dan Belanja Negara dengan menyusun laporan keuangan berupa Laporan Realisasi, Anggaran, Neraca, Laporan Operasional, Laporan Perubahan Ekuitas dan Catatan atas Laporan Keuangan.</p>',	'file/1IVEurb14un84AfSJdroEDKy2VECHH9JGlmLb59u.pdf',	'laporan/CdemDuvphqe80VvpxGe7Y0PO4O0MT8y4IsnO3HTz.png',	'2020-08-25 03:56:12',	'2020-08-25 03:56:12'),
(15,	2,	'Laporan Keuangan Kementerian Komunikasi dan Informatika Tahun 2014',	'laporan-keuangan-kementerian-komunikasi-dan-informatika-tahun-2014-WNnjA',	'<p>Laporan keuangan ini diharapkan dapat memberika informasi yang berguna kepada para pemakai laporan khususnya sebagai sarana untuk meningkatkan akuntabilitas pertanggungjawaban dan transparansi pengelolaan keuangan negara pada Kementerian Komunikasi dan Informatika. Disamping itu laporan keuangan ini juga dimaksudkan juga untuk memberikan informasi kepada manajemen dan pengambilan keputusan dalam usaha untuk mewujudkan tata kelola pemerintahan yang baik (good governance).</p>',	'file/XyNA1TihScIimEbtKe4ShFwKBN3XtFCfWTqIQxed.pdf',	'laporan/0BCAuNYNJjOJVU1TTklEvdjU27tFz3DTkKJwMirr.png',	'2020-08-25 03:58:41',	'2020-08-25 03:58:41'),
(17,	3,	'Laporan Tahunan Direktorat Jenderal Penyelenggaraan Pos dan Informatika Kementerian Komunikasi dan Informatika Tahun 2019',	'laporan-tahunan-direktorat-jenderal-penyelenggaraan-pos-dan-informatika-kementerian-komunikasi-dan-informatika-tahun-2019-PVJVN',	'<p>Laporan Tahunan ini menyajikan berbagai informasi penting terkait seputar industri pos, telekomunikasi, dan penyiaran serta informasi terkait prestasi, kegiatan, dan capaian kinerja program prioritas Direktorat Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) sepanjang tahun 2019. Tolak ukur keberhasilan pencapaian kinerja organisasi Direktorat Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) tercermin dari pencapaian target atas 13 Indikator Kinerja Utama (IKU) dari 13 Indikator Kinerja Utama (IKU) yang ditetapkan dalam Perjanjian Kinerja (PK) Direktur Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) dengan Menteri Komunikasi dan Informatika Tahun 2019. Secara keseluruhan capaian dari kinerja Direktorat jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) tahun 2019 adalah sebesar 99,11%.</p>\r\n\r\n<p><strong>Optimalisasi Penerimaan Negara Bukan Pajak (PNBP)</strong></p>\r\n\r\n<p>Dari sisi kinerja Penerimaan Negara Bukan Pajak (PNBP) Direktorat Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) berhasil mengumpulkan Rp 1,153 Triliun atau sebesar 104,64% dari target Rp 1,102Triliun.</p>\r\n\r\n<p><strong>Komitmen Dalam Rangka Mewujudkan Pelayanan Prima Kepada Masyarakat</strong></p>\r\n\r\n<p>Tahun 2019 menandakan sebuah catatan penting dalam memberikan pelayanan kepada masyarakat dengan diraihnya prestasi penghargaan TOP 99 Inovasi Pelayanan Publik berupa Sistem Perizinan Online dan Pelayanan Prima Ditjen PPI Hebat Andal Berbasis Layanan Teknologi Informasi (SIPPPDIHATI) dimana sistem ini memangkas birokrasi perizinan menjadi lebih mudah, cepat, dan transparan. Seluruh proses pengajuan izin dapat termonitor melalui sistem e-ticketing dan proses pengajuan sampai dengan penerbitan izin yang semula membutuhkan waktu berhari-hari dipangkas menjadi hanya dalam hitungan jam</p>\r\n\r\n<p><strong>Membentuk Sumber Daya Manusia Ditjen PPI Yang Berkualitas</strong></p>\r\n\r\n<p>Pengembangan Sumber Daya Manusia di lingkungan Direktorat Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI) secara menyeluruh guna memastikan terbentuknya organisasi yang baik serta kompetitif dalam mendukung tujuan Direktorat Jenderal Penyelenggaraan Pos dan Informatika (Ditjen PPI). Pada Tahun 2019 Direktorat Jenderal Penyelengga - raan Pos dan Informatika telah berhasil mengadakan bimbingan teknis (Bimtek) untuk para pegawainya, diantaranya Bimtek terkait Akuntabilitas Kinerja, Bim - tek Collaborative Creative Learning and Action for Sustainable Solutions (Co-CLASS) Ekonomi Digital untuk Daerah 3T (Terdepan, Terluar, dan Tertinggal)/3T Go Digital, Bimtek Diplomacy Course, Workshop In - fografis, Bimtek Quality Management System tujuan diberikannya beasiswa tersebut adalah untuk pen - ingkatan kapasitas pegawai Ditjen PPI. Pencapaian yang telah kami lakukan tidaklah terlepas dari kerja keras semua pihak dan seluruh satuan kerja di lingkungan Direktorat Jenderal Pos dan Informatika serta para penyelenggara indus - tri di bidang pos dan informatika. Oleh karenanya kami mengucapkan terima kasih atas kontribusi, dukungan, dan kerja sama yang telah diberikan dalam penyelesaian laporan tahunan ini.</p>\r\n\r\n<p><strong>Tantangan Ke Depan</strong></p>\r\n\r\n<p>Pada tahun 2020 Direktorat Jenderal Penyeleng - garaan Pos dan Informatika (Ditjen PPI) mengemban amanah dalam memberikan kontribusi pendapatan di sektor pos, telekomunikasi, dan penyiaran untuk pertumbuhan GDP Nasional melalui ekonomi digital. Untuk mencapai hal tersebut dibutuhkan strategi dan sinergi seluruh unit yang terlibat agar lebih terarah, terukur, dan terstruktur. Oleh karenanya pada tahun 2020 prioritas program akan lebih diarahkan kepada upaya dalam rangka mewujudkan ekonomi digital. Peningkatan ekonomi digital didukung oleh keterse - diaan akses internet yang memadai khususnya me - lalui jaringan fixed broadband. Untuk mewujudkan hal tersebut, Direktorat Jenderal Penyelenggaraan Pos dan Informatika memiliki program percepatan penetrasi pitalebar berupa pemetaan Supply dan Demand Nasional, fasilitasi penyediaan akses tetap pitalebar (fixed broadband) yang dilakukan di 1000 SSL (Satuan Sambungan Layanan) yang mencakup 50 desa di 12 provinsi, dan sinergi antara pemerintah&nbsp;Pusat dan Daerah dalam pembangunan infrastruktur pasif telekomunikasi dan kebijakan yang memadai agar pembangunan infrastruktur pasif telekomunikasi dapat dengan mudah digelar, berbiaya rendah, dan dapat digunakan bersama baik untuk penyelenggara telekomunikasi maupun kebutuhan utilitas lainnya. Program tersebut menjadi kontribusi dan intervensi Pemerintah sebagai regulator dan fasilitator dalam hal pemerataan akses internet melalui jaringan fixed broadband.</p>\r\n\r\n<p>Kami berharap penerbitan laporan tahunan ini dapat menjadi bahan referensi bagi seluruh jajaran Direktorat Jenderal Penyelenggaraan Pos dan Informatika dan dapat menciptakan sinergitas antara pemerintah dengan masyarakat luas khususnya para investor di bidang pos, telekomunikasi, dan penyiaran, serta dapat meningkatkan kepercayaan masyarakat kepada pemerintah untuk membangun Indonesia yang lebih baik dan lebih maju.</p>',	NULL,	'laporan/VtC6nVz1w2Uz0OfvSBxrINymEVDMVvETKm8BsTsi.png',	'2020-08-25 07:42:51',	'2020-08-25 07:42:51'),
(18,	3,	'Laporan Tahunan Kementerian Komunikasi dan Informatika Tahun 2018',	'laporan-tahunan-kementerian-komunikasi-dan-informatika-tahun-2018-oAM9r',	'<p>Untuk Lporan Bisa Di Baca Dengan Link Di Atas</p>',	'file/85EGqxsLmfLVSLFBfLzJhUzDu3OjNB43AJoxkSHs.pdf',	'default.jpg',	'2020-08-25 08:36:22',	'2020-08-25 08:39:07'),
(19,	3,	'Unduh Laporan Tahunan Ditjen Penyelenggaraan Pos dan Informatika (PPI) Tahun Anggaran 2017',	'unduh-laporan-tahunan-ditjen-penyelenggaraan-pos-dan-informatika-ppi-tahun-anggaran-2017-iAh6q',	'<p>Untuk Laporan Bisa Di Baca Dengan Link Di Atas</p>',	'file/7EjUlSBTu5e6UVaNjFUQ2NIltqS0SJS0k5bUpKGd.pdf',	'laporan/47OzHOZrIFHBQQFtiJKAbLieaz64HVBlvQJ5OpZ0.png',	'2020-08-25 08:44:03',	'2020-08-25 08:44:03'),
(20,	4,	'Laporan Kinerja Sekretariat Jenderal Kementerian Komunikasi dan Informatika Tahun 2019',	'laporan-kinerja-sekretariat-jenderal-kementerian-komunikasi-dan-informatika-tahun-2019-pKCVg',	'<p>Segala puji syukur kami ucapkan kepada Tuhan Yang Maha Esa karena telah memberikan rahmat dan karunia-Nya sehingga Laporan Kinerja (LKj) Sekretariat Jenderal (Setjen) Kementerian Komunikasi dan Informatika (Kominfo) Tahun 2019 dapat diselesaikan. Laporan Kinerja Setjen Kementerian Kominfo Tahun 2019 ini merupakan bentuk pertanggungjawaban atas semua kinerja Sekretariat Jenderal yang telah dilakukan melalui pelaksanaan program dan kegiatan selama periode tahun 2019.</p>\r\n\r\n<p>Penyusunan Laporan Kinerja Sekretariat Jenderal Kementerian Kominfo Tahun 2019 berpedoman pada Peraturan Presiden Republik Indonesia Nomor 29 Tahun 2014 tentang Sistem Akuntabilitas Kinerja Instansi Pemerintah, serta Peraturan Menteri Pendayagunaan Aparatur Negara dan Reformasi Birokrasi Nomor 53 Tahun 2014 tentang Petunjuk Teknis Penyusunan Perjanjian Kinerja, Pelaporan Kinerja, dan Tata Cara Reviu Atas Laporan Kinerja Instansi Pemerintah.</p>\r\n\r\n<p>Laporan Kinerja Sekretariat Jenderal ini menyajikan hasil capaian kinerja Sekretariat Jenderal tahun 2019 berdasarkan realisasi dan capaian target yang ditetapkan dalam Perjanjian Kinerja Sekretariat Jenderal Tahun 2019. Dari Laporan Kinerja ini diharapkan dapat memberikan informasi atas kinerja Sekretariat Jenderal yang terukur kepada pemberi mandat atas kinerja yang telah dan seharusnya dicapai serta sebagai upaya perbaikan untuk meningkatkan kinerja di Sekretariat Jenderal Kementerian Kominfo pada masa yang akan datang.</p>',	'file/FWERlDsPK2fnh2FfZ9jHyCksyw9nOvJFbYljxnxV.pdf',	'laporan/ODvi8WhzTerPkRJLdxORwXK1WjJ3enhxwEpk3c2G.png',	'2020-08-25 09:24:07',	'2020-08-25 09:24:07'),
(21,	4,	'Laporan Kinerja Direktorat Jenderal Penyelenggaraan Pos dan Informatika Kementerian Kominfo Tahun 2019',	'laporan-kinerja-direktorat-jenderal-penyelenggaraan-pos-dan-informatika-kementerian-kominfo-tahun-2019-AUEFX',	'<p>Tanpa terasa tahun 2019 telah berakhir, sementara masih banyak target-target yang harus dicapai untuk dapat mewujudkan apa yang menjadi target pemerintah dalam kerangka Rencana Pembangunan Jangka Menengah Nasional 2015&ndash;2019. Terhadap hal tersebut dilakukan evaluasi tahunan secara menyeluruh untuk dapat melihat sejauh mana keberhasilan kinerja dari Direktorat Jenderal Penyelengaraan Pos dan Informatika dalam rangka pencapaian kinerja dari target-target yang telah ditetapkan baik keluaran (output) maupun kemanfaatannya (outcomes). Evaluasi dimaksud akan di jabarkan dalam Laporan Kinerja Direktorat Jenderal Penyelenggaraan Pos dan Informatika tahun 2019, yang mana hal ini juga sejalan dengan amanat dari Peraturan Presiden Republik Indonesia Nomor 29 tahun 2014 tentang Sistem Akuntabilitas Kinerja Instansi Pemerintah (SAKIP) yang mewajibkan untuk membuat Laporan Kinerja Pemerintah tiap akhir tahun anggaran untuk dilaporkan kepada Presiden Republik Indonesia.</p>\r\n\r\n<p>Melalui Laporan Kinerja Direktorat Jenderal Penyelenggaraan Pos dan Informatika tahun 2019 ini diharapkan dapat memberi informasi yang komprehensif kepada masyarakat pada umumnya dan menjadikan bahan evaluasi dalam penyusunan Rencana Strategis 2020&ndash;2024 Direktorat Jenderal Penyelenggaraan Pos dan Informatika agar dapat dilaksanakan secara efektif dan efisien.</p>',	NULL,	'laporan/cLzESc8osyz4veZGlQaQIyZAU41LVk0zjxBbkUXI.png',	'2020-08-26 02:52:27',	'2020-08-26 02:52:27'),
(22,	4,	'Laporan Kinerja Kementerian Komunikasi dan Informatika Tahun 2019',	'laporan-kinerja-kementerian-komunikasi-dan-informatika-tahun-2019-9QAMD',	'<p>Laporan Kinerja Kementerian Komunikasi dan Informatika merupakan bentuk dari pertanggungjawaban atas kinerja pencapaian visi dan misi yang dijabarkan dalam sasaran strategis. Sasaran strategis tersebut mengacu pada Rencana Pembangunan Jangka Menengah (RPJMN) periode 2015&mdash;2019 dan Rencana Strategis Kementerian Komunikasi dan Informatika Tahun 2015&mdash;2019. Uraian sasaran strategis tersebut kemudian ditetapkan dalam bentuk Perjanjian Kinerja (PK) Kementerian Komunikasi dan Informatika Tahun 2019 yang merupakan implementasi dari empat program prioritas, yaitu: Infrastruktur Akses Pitalebar (Broadband), Digitalisasi Penyiaran Televisi, Tata Kelola Internet dan Government Public Relations (GPR), serta kinerja lainnya yang memiliki dampak positif bagi masyarakat.</p>\r\n\r\n<p>Dalam implementasi keempat program prioritas diatas, Kementerian Komunikasi dan Informatika menetapkan 4 (empat) Sasaran Strategis yang memuat perspektif stakeholder, perspektif layanan maupun perspektif internal. Perspektif stakeholder, yaitu</p>\r\n\r\n<ol>\r\n	<li>pemerataan Jangkauan Layanan Komunikasi dan Informatika di wilayah Indonesia persentase capaiannya sebesar 94,79 persen;&nbsp;</li>\r\n	<li>Digitalisasi Sektor-sektor Prioritas Nasional persentase capaiannya sebesar 98,83 persen.</li>\r\n</ol>\r\n\r\n<p>Sedangkan capaian sasaran strategis dalam Pers- pektif Layanan, yakni (1) Peningkatan Kepuasan Masyarakat Terhadap Informasi Publik Terkait Kebijakan dan Program Prioritas Pemerintah persentase capaiannya sebesar 100 persen.</p>\r\n\r\n<p>Terakhir adalah capaian sasaran strategis dalam Perspektif Proses Internal, dapat dijelaskan sebagai berikut: (1) Peningkatan Kualitas Tata Kelola Kementerian Komunikasi dan Informatika persentase capaiannya adalah 99,74 persen.</p>\r\n\r\n<p>Secara umum pencapaian sasaran strategis yang telah ditetapkan dalam tahun 2019 telah sesuai dengan yang ditargetkan. Namun demikian, masih terdapat beberapa indikator kinerja yang masih belum mencapai target yang ditetapkan. Untuk itu, Kementerian Komunikasi dan Informatika akan senantiasa berupaya dan bekerja lebih keras lagi, serta menyempurnakan kebijakan yang ada untuk lebih mengoptimalkan pencapaian sasaran strategis, sehingga diharapkan di tahun berikutnya capaian semua sasaran strategis dapat lebih optimal.</p>',	NULL,	'laporan/ob8zz0ijpBw69ORImRUDNIiWNhFEbdT5IhnHGalg.png',	'2020-08-26 02:56:31',	'2020-08-26 02:56:31'),
(23,	4,	'DAMO: Memori Pertanggungjawaban Menteri Komunikasi dan Informatika Republik Indonesia 2014-2019',	'damo-memori-pertanggungjawaban-menteri-komunikasi-dan-informatika-republik-indonesia-2014-2019-OzJ1B',	'<p><strong>DAMO: Discovery,&nbsp;Adventure, Momentum,&nbsp;dan Outlook di Kominfo </strong></p>\r\n\r\n<p><em>Memori Pertanggungjawaban Menteri Komunikasi&nbsp;dan Informatika Republik Indonesia 2014-2019</em></p>\r\n\r\n<p>Ketika Kabinet Kerja Presiden Joko Widodo dan Wakil Presiden Jusuf Kalla memulai tugasnya pada tahun 2014, dunia sedang memasuki era ekonomi digital yang minim sekali tuntunan dan preseden dari era sebelumnya. Yang kita tahu saat itu adalah bahwa masyarakat kita merupakan &ldquo;early adopters&rdquo; yang sangat baik bagi teknologi dan layanan-layanan di Internet, terutama media sosial, yang diselenggarakan di atas jaringan internet mobile.</p>\r\n\r\n<p>Namun ternyata masyarakat kita juga tak kalah sigap dalam mengantisipasi layanan-layanan baru yang bersifat disruptif namun memiliki nilai tambah yang besar bagi mereka. Oleh sebab itu justru pada masa transisi tersebut, muncul berbagai bisnis baru yang sukses karena menempuh model bisnis yang sama sekali baru dan dikelola dengan cara-cara baru oleh orang-orang baru (yang umumnya kaum muda) yang justru belum terkontaminasi oleh pengelolaan bisnis gaya sebelumnya.</p>\r\n\r\n<p>Dalam tikungan tajam digitalisasi itu, pemerintah juga harus mengambil langkah yang pas, sesuai dengan perkembangan. Kominfo yang sebelumnya menyandang predikat dan fungsi sebagai regulator tidak bisa lagi hanya berkutat pada peran yang relatif pasif itu jika hendak menyediakan ruang yang kondusif bagi perkembangan ekosistem digital.</p>\r\n\r\n<p>&nbsp;</p>',	NULL,	'laporan/tB9NWm8xCGSb8GDa67SBv2slL3b8ycIF8xhxTIvY.jpeg',	'2020-08-26 02:59:44',	'2020-08-26 02:59:44'),
